import React, { useState, useEffect } from "react";
import "../styles/Profile.css";

const Profile = () => {
  const [userData, setUserData] = useState({
    name: "Not Available",
    email: "Not Available",
    password: "********",
    profilePic: "",
  });

  // Load user details from localStorage
  useEffect(() => {
    const storedData = localStorage.getItem("userData");
    if (storedData) {
      const parsedData = JSON.parse(storedData);
      setUserData((prev) => ({
        ...prev,
        name: parsedData.name || "Not Available",
        email: parsedData.email || "Not Available",
        password: parsedData.password ? "********" : "Not Available",
      }));
    }

    const storedPic = localStorage.getItem("profilePic");
    if (storedPic) {
      setUserData((prev) => ({ ...prev, profilePic: storedPic }));
    }
  }, []);

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setUserData((prev) => ({ ...prev, profilePic: reader.result }));
        localStorage.setItem("profilePic", reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="profile-page">
      <div className="profile-card">
        <h2>My Profile</h2>

        <div className="profile-photo">
          <img
            src={
              userData.profilePic ||
              "https://cdn-icons-png.flaticon.com/512/149/149071.png"
            }
            alt="Profile"
          />
          <label htmlFor="upload" className="upload-btn">
            Upload Photo
          </label>
          <input
            id="upload"
            type="file"
            accept="image/*"
            onChange={handlePhotoUpload}
          />
        </div>

        <div className="profile-info">
          <p>
            <strong>Name:</strong> {userData.name}
          </p>
          <p>
            <strong>Email:</strong> {userData.email}
          </p>
          <p>
            <strong>Password:</strong> {userData.password}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Profile;
