import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/AuthPages.css";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Login Successful!");
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-left">
        <h1>Welcome Back ðŸ‘‹</h1>
        <p className="subtitle">
          Access your freelance dashboard and continue where you left off.
        </p>

        <form className="auth-form" onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email address"
            value={formData.email}
            onChange={handleChange}
            required
          />

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />

          <button type="submit" className="btn-gradient">Login</button>
        </form>

        <p className="switch-text">
          New to SB Works? <Link to="/signup" className="link">Create account</Link>
        </p>
      </div>

      <div className="auth-right">
        <img
          src="https://cdni.iconscout.com/illustration/premium/thumb/login-concept-illustration-6763523-5604765.png"
          alt="Login illustration"
        />
      </div>
    </div>
  );
};

export default Login;
